import React from 'react'
import PropTypes from 'prop-types'

const CustomButton = ({ title, backgroundColor, textColor, hoverclass, borderColor="indigo", onclick }) => {
  return (
    <button className={`${textColor} ${backgroundColor} ${hoverclass} px-8 py-2 rounded
     flex flex-row gap-3 items-center cursor-pointer border border-${borderColor}`} onclick={onclick}>
      {title}
    </button>
  )
}

CustomButton.propTypes = {
  title: PropTypes.string.isRequired,
  textColor: PropTypes.string,
  backgroundColor: PropTypes.string,
  borderColor: PropTypes.string,
  hoverclass: PropTypes.string,
  onclick:PropTypes.func
}

export default CustomButton
