import React, { useEffect, useState } from 'react'
import Home from './Home/Home';
import CustomImage from './CustomImage';

const Books = () => {
  const [mybooks, setMyBooks] = useState([]);

  useEffect(() => {
    fetch("http://localhost:2000/books")
      .then((response) => response.json())
      .then((data) => {
        setMyBooks(data);
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);


  return (
    <div className='mx-[10%]'>
      <div className='flex items-center flex-col justify-center py-10 gap-8'>
        <div className='text-5xl font-bold '>Books</div>
        <div className='w-[300px]'>Porttitor in nibh id aliquet quam aliquam aliquet pulvinar
          integer dolor quis elementum, dui cursus nisi, nunc viverra nulla fringilla.</div>
      </div>
      <div>
        <Home />
      </div>
      <div>
        <div className='grid grid-cols-4'>
          {mybooks?.map((book) => (
            <div key={book?.id}>
              <div className="flex flex-col my-6 bg-white shadow-sm rounded-lg  font-garamond">
                <div className='w-[280px]'>
                  <CustomImage name="book2" />
                </div>
                <div className="text-left flex flex-col gap-1 p-4">
                  <span className="pt-2 text-abbey">{book?.type}</span>
                  <span>{book?.name}</span>
                  <span>{book?.price}</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  )
}

export default Books
