import React from 'react';
import CustomImage from '../CustomImage';
import CustomButton from '../CustomButton';

const HomeBooks = ({title,data}) => {
    const getbooks = data.slice(0,4)
    console.log("first",getbooks)

    const books = {
        "books": [
            {
                "id": 1,
                "name": "The Great Adventure",
                "prize": "$19.99",
                "link": "https://example.com/book1-image.jpg",
                "genre": "Adventure"
            },
            {
                "id": 2,
                "name": "Mystery of the Lost City",
                "prize": "$14.99",
                "link": "https://example.com/book2-image.jpg",
                "genre": "Mystery"
            },
            {
                "id": 3,
                "name": "Science for the Curious Mind",
                "prize": "$24.99",
                "link": "https://example.com/book3-image.jpg",
                "genre": "Science"
            },
            {
                "id": 4,
                "name": "Cooking Made Simple",
                "prize": "$12.99",
                "link": "https://example.com/book4-image.jpg",
                "genre": "Cookbook"
            },
            {
                "id": 5,
                "name": "Journey Through Time",
                "prize": "$17.50",
                "link": "https://example.com/book5-image.jpg",
                "genre": "Historical Fiction"
            },
            {
                "id": 6,
                "name": "The Hidden Truth",
                "prize": "$22.99",
                "link": "https://example.com/book6-image.jpg",
                "genre": "Thriller"
            }
        ]
    };


    return (
        <div className="flex flex-col justify-center">
            <div className="font-garamond text-5xl py-[2%]">
                <span>{title}</span>
            </div>
            <div className='grid grid-cols-4'>
                {getbooks?.map((book) => (
                    <div key={book?.id}>
                        <div className="flex flex-col my-6 bg-white shadow-sm rounded-lg  font-garamond">
                            <div className='w-[280px]'>
                                <CustomImage name="book2" />
                            </div>
                            <div className="text-left flex flex-col gap-1 p-4">
                                <span className="pt-2 text-abbey">{book?.type}</span>
                                <span>{book?.name}</span>
                                <span>{book?.price}</span>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
            <div className='flex justify-center'>
                <CustomButton title='Show More' backgroundColor='bg-white' textColor='text-indigo'
                    hoverclass={`hover:bg-indigo hover:text-white`}
                    borderClass={`border-indigo`}
                />
            </div>

        </div>
    );
};

HomeBooks.prototype = {
    title: String,
    data:Array
}
export default HomeBooks;
