import React from 'react';
import CustomImage from '../CustomImage';

const HomeBooks = () => {
    const books = {
        "books": [
            {
                "id": 1,
                "name": "The Great Adventure",
                "prize": "$19.99",
                "link": "https://example.com/book1-image.jpg"
            },
            {
                "id": 2,
                "name": "Mystery of the Lost City",
                "prize": "$14.99",
                "link": "https://example.com/book2-image.jpg"
            },
            {
                "id": 3,
                "name": "Science for the Curious Mind",
                "prize": "$24.99",
                "link": "https://example.com/book3-image.jpg"
            },
            {
                "id": 4,
                "name": "Cooking Made Simple",
                "prize": "$12.99",
                "link": "https://example.com/book4-image.jpg"
            },
            {
                "id": 5,
                "name": "Journey Through Time",
                "prize": "$17.50",
                "link": "https://example.com/book5-image.jpg"
            },
            {
                "id": 6,
                "name": "The Hidden Truth",
                "prize": "$22.99",
                "link": "https://example.com/book6-image.jpg"
            }
        ]
    };

    return (
        <div className="">
            <div className="font-garamond text-5xl py-[10%]">
                <span>Best Selling Books</span>
            </div>

            {books.books.map((book) => (
                <div key={book.id}>{book.name}</div>
            ))}

            <div>
                <div className="flex flex-col my-6 bg-white shadow-sm rounded-lg w-96 font-garamond">
                    <CustomImage name="book2" />
                    <div className="text-left flex flex-col gap-1 p-4">
                        <span className="pt-2 text-abbey">Fantasy</span>
                        <span>The Born of APLEX</span>
                        <span>$26.00</span>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default HomeBooks;
