import React, { useContext } from "react";
import PropTypes from "prop-types";

const GlobalContext = (props) => {
  const [showCustomToast, setShowCustomToast] = React.useState(
    props.showCustomToast
  );
  const [updateCustomToast, setUpdateCustomToast] = React.useState(
    props.updateCustomToast
  );
  const [showToast, setShowToast] = React.useState(false);
  const [toastType, setToastType] = React.useState(props.toastType);
  const [toastText, setToastText] = React.useState(props.toastText);
  const hideToast = () => {
    setShowToast(false);
  };

  const updataCustomToast = (_toastType, toastText) => {
    setShowCustomToast(true);
    setToastType(_toastType);
    setToastText(toastText);
  }

  return (
    <GlobalContext.Provider
      value={{
        useGlobalContext,
        updateCustomToast: updataCustomToast,
        showCustomToast: showCustomToast,
        toastType: toastType,
        toastText: toastText,
        hideToast: hideToast,
        setShowCustomToast: setShowCustomToast,
        setUpdateCustomToast: setUpdateCustomToast,
        setShowToast: setShowToast,
      }}
    >
      {props.children}
    </GlobalContext.Provider>
  );
};

const useGlobalContext = () => {
  const GlobalContextObject = useContext(GlobalContext);
  if(GlobalContextObject === undefined) {
    throw new Error("useGlobalContext must be used within a GlobalContextProvider");
  }
  return GlobalContextObject;
}

GlobalContext.propTypes = {
  updateCustomToast: PropTypes.func.isRequired,
  showCustomToast: PropTypes.bool.isRequired,
  toastType: PropTypes.oneOf(["success", "error", "info"]).isRequired,
  toastText: PropTypes.string.isRequired,
};

export  {useGlobalContext, GlobalContext};
