import React from 'react'
import PropTypes from 'prop-types'

const CustomButton = ({ title, backgroundColor, textColor, hoverTextColor, borderColor="indigo" }) => {
  return (
    <div className={`${textColor} ${backgroundColor} hover:${hoverTextColor} px-8 py-2 rounded
     flex flex-row gap-3 items-center cursor-pointer border border-${borderColor}`}>
      {title}
    </div>
  )
}

CustomButton.propTypes = {
  title: PropTypes.string.isRequired,
  textColor: PropTypes.string,
  backgroundColor: PropTypes.string,
  borderColor: PropTypes.string,
}

export default CustomButton
