import React, { useEffect, useState } from 'react'

const Books = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetch("http://localhost:2000/books")
      .then((response) => response.json())
      .then((data) => {
        setBooks(data);
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
    <div>

      {books.map((book) => (
        <div key={book?.id}>
          <div className="flex flex-col my-6 bg-white shadow-sm rounded-lg  font-garamond">
            <div className='w-96'>
              <CustomImage name="book2" />
            </div>
            <div className="text-left flex flex-col gap-1 p-4">
              <span className="pt-2 text-abbey">{book?.genre}</span>
              <span>{book?.name}</span>
              <span>{book?.prize}</span>
            </div>
          </div>
        </div>
      ))}


    </div>
  )
}

export default Books
