import React, { useEffect } from "react";
import { useGlobalContext } from "../context/GlobalContext";

const CustomToast = () => {
  const { toastText, toastType, showCustomToast, hideToast } =
    useGlobalContext();

  useEffect(() => {
    const timer = setTimeout(() => {
      hideToast();
    }, 3000);
    return () => clearTimeout(timer);
  }, [showCustomToast]);
  console.log("showCustomToast", showCustomToast);

  const headingTextColor = () => {
    switch (toastType) {
      case "SUCCESS":
        return "text-green-500";
      case "ERROR":
        return "text-red-500";
      case "INFO":
        return "text-blue-500";
      default:
        return "";
    }
  };

  const headingText = () => {
    switch (toastType) {
      case "SUCCESS":
        return "SUCCESS";
      case "ERROR":
        return "ERROR";
      case "INFO":
        return "INFO";
      default:
        return "";
    }
  };

  const toastIcon = () => {
    switch (toastType) {
      case "SUCCESS":
        return "✓";
      case "ERROR":
        return "✗";
      case "INFO":
        return "ℹ️";
      default:
        return "";
    }
  };

  return (
    <>
      {showCustomToast && (
        <div
          className={`fixed w-[20%] top-0 right-0 m-4 p-4 bg-white shadow-lg rounded-lg ${headingTextColor()}`}
        >
          <div className=" bg-white flex flex-col items-center gap-2">
            <span >{toastIcon()} {headingText()} {toastText}</span>
          </div>
        </div>
      )}
    </>
  );
};

CustomToast.propTypes = {};

export default CustomToast;
