import React, { useEffect, useState } from "react";
import CustomInputFeild from "../CustomInputFeild";
import CustomImage from "../CustomImage";
import CustomButton from "../CustomButton";
import DropdownSingleSelect from "../Dropdowns/DropdownSingleSelect";
import { Countries } from "../../helper/helper";
import { getProfile, profileBuild } from "../../services/service";
import { useGlobalContext } from "../../context/GlobalContext";

const ProfilePage = () => {
  const [userData, setUserData] = React.useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    address: "",
    phoneNumber: "",
    city: "",
    state: "",
    country: "",
    pincode: "",
    profilePicture: "",
  });

  const [statelist, setStateList] = useState([]);
  const { updateCustomToast } = useGlobalContext();
  const countryList = Countries.map((item) => {
    return item.name;
  });

  const handleCountryChange = () => {
    const selectedCountry = Countries.find(
      (item) => item.name === userData.country
    );

    const stateList = selectedCountry?.states || [];

    setStateList(stateList);
  };

  useEffect(() => {
    handleCountryChange();
  }, [userData]);

  useEffect(() => {
    getProfile(username, payload).then((res) => {
      if (res.statusCode < 201 && res.statusCode > 400) {
        setUserData(res?.data);
      } else {
        updateCustomToast("Error", "Not able to update details");
      }
    });
  });

  const handleSave = (username) => {
    let payload = {
      address: userData.address,
      pinCode: userData.pincode,
      state: userData.state,
      country: userData.country,
      city: userData.city,
      phoneNumber: userData.phoneNumber,
    };
    profileBuild(username, payload).then((res) => {
      if (res.statusCode < 201 && res.statusCode > 400) {
        console.log("first", res);
      } else {
        updateCustomToast("Error", "Not able to update details");
      }
    });
  };

  return (
    <div className="flex flex-col">
      <div className="flex flex-col w-full items-start ">
        <div className="w-full ">
          <img
            src={`/images/banner.jpg`}
            className="w-full object-cover"
            alt=""
          />
        </div>
      </div>

      <div className="relative flex flex-col items-center justify-center w-full ">
        <div className="bg-white flex items-center justify-center rounded-full w-[150px] h-[150px] absolute top-[-50px]">
          <div className="h-[50%] w-[50%] ">
            <CustomImage name="userIcon" />
          </div>
        </div>
      </div>

      <div className="flex flex-col gap-5 items-start mt-[10%] mb-[4%] mx-[10%]">
        <div className="w-full grid grid-cols-2 gap-5">
          <CustomInputFeild
            type="text"
            placeholder="Enter your first name"
            name="firstName"
            value={userData.firstName}
            onChange={(e) =>
              setUserData({ ...userData, firstName: e.target.value })
            }
            isEdit={true}
          />
          <CustomInputFeild
            type="text"
            placeholder="Enter your last name"
            name="lastName"
            value={userData.lastName}
            onChange={(e) =>
              setUserData({ ...userData, lastName: e.target.value })
            }
            isEdit={true}
          />
          <CustomInputFeild
            type="email"
            placeholder="Enter your email"
            name="email"
            value={userData.email}
            onChange={(e) =>
              setUserData({ ...userData, email: e.target.value })
            }
            isEdit={true}
          />
          <CustomInputFeild
            type="password"
            placeholder="Enter your password"
            name="password"
            value={userData.password}
            onChange={(e) =>
              setUserData({ ...userData, password: e.target.value })
            }
            isEdit={true}
          />
          <CustomInputFeild
            type="text"
            placeholder="Enter your Address"
            name="address"
            value={userData.address}
            onChange={(e) =>
              setUserData({ ...userData, address: e.target.value })
            }
            isEdit={true}
          />
          <CustomInputFeild
            type="number"
            placeholder="Enter your Phone Number"
            name="phoneNumber"
            value={userData.phoneNumber}
            onChange={(e) =>
              setUserData({ ...userData, phoneNumber: e.target.value })
            }
            isEdit={true}
          />
          <CustomInputFeild
            type="text"
            placeholder="Enter your city"
            name="phoneNumber"
            value={userData.city}
            onChange={(e) => setUserData({ ...userData, city: e.target.value })}
            isEdit={true}
          />
          <DropdownSingleSelect
            data={countryList}
            value={userData.country}
            placeHolder="Select your country"
            onClick={(item) => {
              setUserData({ ...userData, country: item });
            }}
            handleChange={handleCountryChange}
            isEdit={true}
          />
          <DropdownSingleSelect
            data={statelist}
            value={userData.state}
            placeHolder="Select your state"
            onClick={(item) => setUserData({ ...userData, state: item })}
            isEdit={true}
          />
          <CustomInputFeild
            type="number"
            placeholder="Enter your pincode"
            name="pincode"
            value={userData.pincode}
            onChange={(e) =>
              setUserData({ ...userData, pincode: e.target.value })
            }
            isEdit={true}
          />
        </div>
      </div>

      <div className="flex flex-col gap-5 items-end mb-[4%] mx-[10%]">
        <CustomButton
          title="Save"
          backgroundColor="bg-indigo"
          PaddingX="px-10"
          PaddingY="py-2"
          textColor="text-white"
          myOnClick={handleSave}
          borderClass="border-indigo"
        />
      </div>
    </div>
  );
};

export default ProfilePage;
